ScreenPDMViewBase::~ScreenPDMViewBase()
{

}

void ScreenPDMViewBase::setupScreen()
{

}

void ScreenPDMViewBase::handleKeyEvent(uint8_t key)
{
    if(0 == key)
    {
        //CANPage
        //When hardware button 0 clicked change screen to ScreenCANtest
        //Go to ScreenCANtest with no screen transition
        application().gotoScreenCANtestScreenNoTransition();
    
    }

    if(1 == key)
    {
        //D03
        //When hardware button 1 clicked call virtual function
        //Call updateD03
        updateD03();
    
    }

    if(2 == key)
    {
        //D07
        //When hardware button 2 clicked call virtual function
        //Call updateD07
        updateD07();
    
    }

    if(3 == key)
    {
        //D011
        //When hardware button 3 clicked call virtual function
        //Call updateD011
        updateD011();
    
    }
}
